import sys
import math

def main():
	n, k = map(int, sys.stdin.readline().split())
	tabs = [int(sys.stdin.readline()) for _ in range(n)]
	social_net = []
	test = []
	for tab in tabs:
		if tab == -1:
			social_net.append(tab)
		else:
			test.append(tab)

	tab_counter = 0
	for i in range(k):
		tab_counter += 1
		test.pop(0)
		if tab_counter <= n:
			break

	tab_counter = 0
	for i in range(k, n):
		tab_counter += 1
		social_net.pop(0)
		if tab_counter <= n:
			break

	diff = test.pop(-1) - social_net.pop(-1)
	print(diff)



def __starting_point():
	main()
__starting_point()
