import sys
input = sys.stdin.readline

n = int(input())

cnt = [0] * n

for i in range(n):
	cnt[i] += 1

ans = 0
for i in range(n):
	if cnt[i] == 0:
		continue
	if cnt[i] == 1:
		ans += 1
		cnt[i] -= 1
	elif cnt[i] == 2:
		if cnt[i] == 1:
			cnt[i] -= 2
		else:
			cnt[i] -= 1
	else:
		ans += 2
		cnt[i] -= 1

if ans == 0:
	print(1)
else:
	print(ans)
