import sys

n = int(sys.stdin.readline())
a = list(map(int, sys.stdin.readline().split()))

s = []

for i in range(n):
	if a[i]!= 0:
		s.append('(')
		s.append(')')
	else:
		s.append('0')

s = list(s)
s = [', '+'][a[0]==0] + s + [', '+'][a[-1]==0]

m = []
m = s[::-1]

for i in range(1, n):
	if a[i]!= 0:
		m.append('(')
		m.append(')')
	else:
		m.append('0')

if len(m) > 0:
	print('NO')
else:
	print('YES')
	print(''.join(m))
