n = int(input())
a = list(map(int, input().split()))

def solve(a, n):
	dp = [[0] * 2 for _ in range(n)]
	dp[0][0] = 1
	for i in range(1, n):
		if a[i] % 2 == 0:
			dp[i][0] = dp[i - 1][1]
			dp[i][1] = dp[i - 1][0] + 1
		else:
			dp[i][0] = dp[i - 1][0] + 1
			dp[i][1] = dp[i - 1][1]
	if dp[n - 1][0] % 2 == 0:
		return 'Yes'
	return 'No'

print(solve(a, n))
