n = int(input())
a = list(map(int, input().split()))

l = list(range(1, n+1))
r = list(range(n, 0, -1))

def find(x):
	while x!= l[x]:
		x = l[x]
	return x

def unite(x, y):
	l.append(x)
	r.append(y)

for i in range(n-1):
	unite(a[i], a[i+1])

max_ = 0
for i in range(1, n+1):
	if a[i] - a[i-1] <= r[i-1] - r[i]:
		continue
	else:
		max_ = max(max_, a[i-1] - a[i])

print(max_)
