n = int(input())
a = list(map(int, input().split()))

t = a[:]
for i in range(1, n):
	t[i] = t[i - 1] + 1

r = []
for i in range(n):
	for j in range(i + 1, n):
		l = t[i]
		r.append((l, i - j))

r.sort()
q = []
for (l, i), (r, j) in r:
	q.append(i * n + j)

if not t:
	print('NO')
else:
	print('YES')
	for i in range(q[0]):
		print(i, q[1 + i])
