import sys

def minp():
	return sys.stdin.readline().strip()

def mint():
	return int(minp())

def mints():
	return list(map(int,minp().split()))

def solve():
	n = mint()

	for i in range(len(n)):
		if n[i] == 0:
			continue
		if n[i] > 1:
			return -1
		if n[i] == 1:
			n = n[:i] + n[i+1:]
			if n[i] == 1:
				return -1

	return len(n)

print(solve())
