import sys
readline = sys.stdin.readline

n,a = list(map(int, readline().split()))

# if n == a:
#     return 0

if n % 2 == 0:
	even = n//2
	odd = even + 1
else:
	even = (n-1)//2
	odd = even + 1
cost = 0

def calc(a,cost):
	if cost % 2 == 0:
		return a + calc(even,cost + 1)
	else:
		return a + calc(odd,cost + 1)

print(calc(a,0))
