n, k = list(map(int, input().split()))
if n == 2 and k == 0:
	print(-1)
else:
	m = [[0] * (n + 1) for i in range(n + 1)]
	for i in range(1, n + 1):
		m[i][i] = 0
	for i in range(1, n + 1):
		for j in range(1, i + 1):
			m[i][j] = 1 if m[i - 1][j - 1] == 1 else 0
	for i in range(n, 0, -1):
		for j in range(1, i + 1):
			m[i][j] = 1 if m[i][j - 1] == 1 else 0
	for i in range(1, n + 1):
		print(''.join(map(str, m[i])))
