import sys

def solve():
	k1, k2, k3 = [int(x) for x in input().split()]
	t = (k1 + k2 + k3) * 3
	m = t // k1
	if k1 % 2 == 1:
		m += k1 // 2
	if k2 % 2 == 1:
		m += k2 // 2
	if k3 % 2 == 1:
		m += k3 // 2
	if m % 3 == 0:
		print("YES")
	else:
		print("NO")


def __starting_point():
