def rn():
	return input()

def rn_():
	return list(map(int, input().split()))

def solve(n):
	if n == 0:
		return 0

	if n == 1:
		return 1

	if n % 2 == 0:
		return rn_()

	x = int(n / 2)

	if x % 2 == 1:
		return rn_() + x

	return rn_()

