n = int(input())

s = input()

stack = []

ans = []

for i in range(len(s)):
	if len(stack) == 0:
		ans.append(s[i])
		stack.append("|")
	elif s[i] == ']':
		if len(stack)!= 0:
			ans.append("|" + stack[-1] + "|")
			stack.pop()
		else:
			ans.append("|")
			stack.append("|")
	else:
		ans.append(s[i])
		stack.append("+")

print("".join(ans))
