skewer_pos[0] = 1

for i in range(1, n + 1):
	skewer_pos[i] = (skewer_pos[i - 1] + 1) % 2

pos = [0] * (n + 1)
pos[1] = 1

for i in range(2, n + 1):
	pos[i] = (pos[i - 1] + 1) % 2

cnt = 0
kewl = 0
for pos_i in range(2, n + 1):
	if p[pos_i - 1] == b[pos_i - 1]:
		kewl += 1
		cnt += 1
	else:
		kewl = 0

if kewl == 0:
	print(1)
