n, m, k = map(int, input().split())
if n == m and k == 1:
	print('YES')
	print('\n'.join([' '.join(map(str, list(range(1, n + 1)))) for n in range(1, n + 1)]))
elif n == m * k:
	print('YES')
	print('\n'.join([' '.join(map(str, list(range(1, m + 1)))) for m in range(1, m + 1)]))
elif k * (k - 1) * m == n * (n - 1) * k:
	print('YES')
	print('\n'.join([' '.join(map(str, list(range(1, n + 1)))) for n in range(1, n + 1)]))
else:
	print('NO')
