from sys import stdin

n = int(stdin.readline())

s = stdin.readline()
a = list(s)
b = [-1]
c = [0]
d = {'U': 0, 'D': 1, 'L': 2, 'R': 3}
for i in range(n):
	if a[i] == 'U':
		c.append(d[a[i]])
	elif a[i] == 'D':
		c.append(c[-1] + 1)
	elif a[i] == 'L':
		c.append(c[-1] - 1)
	else:
		c.append(0)

sol = 1000000001

for i in range(len(c) - 1):
	sol = min(sol, c[i] + 1 + b[i + 1])

if sol > 1000000001:
	print(-1)
else:
	print(sol)
