import sys
input = sys.stdin.readline

t=int(input())
for tests in range(t):
	n,x=list(map(int,input().split()))
	S=input().strip()

	MIN=0
	MAX=10**9

	ANS=0

	for i in range(n):
		if S[i]=="1":
			MAX=max(MAX,i)
			MIN=min(MIN,i)
		else:
			ANS+=1

	if MIN==0 and MAX==10**9:
		print(-1)
		continue

	if MAX<=0:
		print(ANS)
		continue

	ANS-=1

	for i in range(1,MAX+1):
		if i*(i+1)/2<=MAX:
			ANS-=1

	print(ANS)
