import sys

def minp():
	return sys.stdin.readline().strip()

def mint():
	return int(minp())

def mints():
	return list(map(int,minp().split()))

def solve():
	n,m,a,b = mints()
	if n == 0:
		return 0
	if m == 1:
		return a
	t = a*(m-1)
	d = n//m
	if d!= 0:
		t += b
	return t

print(solve())
