import math

def solve(a, b):
	if a > b:
		return a
	if b > a:
		return b
	a1 = a
	b1 = b
	c = a1 * b1
	if a1 % 2 == 0:
		c += b1 // 2
		c += b1 // 3
	elif b1 % 2 == 0:
		c += a1 // 2
		c += a1 // 3
	else:
		c += b1 // 2
		c += b1 // 3
	return c

def main():
	a, b = list(map(int, input().split()))
	print(solve(a, b))
	if -1 == input():
		print(-1)
	else:
		print(*[solve(a, b) for i in range(int(input()))])

main()
