input = sys.stdin.readline

t=int(input())
for tests in range(t):
	n,k=list(map(int,input().split()))
	A=list(map(int,input().split()))

	ANS=A
	for i in range(1,n):
		if i%2==0:
			if ANS[i]==0:
				ANS[i]=k
				continue
			if ANS[i]<k:
				ANS[i]+=k
			else:
				ANS[i]=k^ANS[i-1]
		else:
			if ANS[i]==0:
				ANS[i]=k
				continue
			if ANS[i]<k:
				ANS[i]=k^ANS[i-1]
			else:
